node - <<'NODE'
require('dotenv').config();
(async()=>{
  const mysql = require('mysql2/promise');
  const conn = await mysql.createConnection({
    host: process.env.DB_HOST||'localhost',
    user: process.env.DB_USER||'root',
    password: (process.env.DB_PASSWORD||'').trim(),
    database: (process.env.DB_NAME||'FoodRescue').trim(),
    port: Number((process.env.DB_PORT||'3306').trim())
  });
  const tables=['users','donations','pickups','distributions','food_requests','donors','volunteers','receivers','centers'];
  for (const t of tables) {
    const [r] = await conn.query(`SELECT COUNT(*) AS c FROM \`${t}\``);
    console.log(t, r[0].c);
  }
  await conn.end();
})();
NODE
